# Write your MySQL query statement below
SELECT s.student_id, s.student_name, j.subject_name, COALESCE(COUNT(e.student_id), 0) AS attended_exams FROM Students AS s CROSS JOIN Subjects AS j LEFT JOIN Examinations AS e ON s.student_id = e.student_id AND j.subject_name = e.subject_name GROUP BY s.student_id, s.student_name, j.subject_name ORDER BY s.student_id, j.subject_name